/* 準備環境與資料 */
CREATE DATABASE [MergeLogRecordSample]
GO

USE [MergeLogRecordSample]
GO

--提供 MERGE 來源資料表
CREATE TABLE [dbo].[StockList]
(
	[No]			INT IDENTITY(1,1),

	[Schema]		CHAR(4)			NOT NULL,
	[ISINCode]		CHAR(12)		NOT NULL,
	[PublishDate]	DATE			NOT NULL,

	[Name]			NVARCHAR(10)	NOT NULL,
	[Category]		NVARCHAR(10)	NOT NULL,
	[Industry]		NVARCHAR(20)	NULL,
	[CFICode]		VARCHAR(6)		NULL,

	CONSTRAINT [pk_StockList] PRIMARY KEY ([No]),

	CONSTRAINT [un_StockList_Schema] UNIQUE ([Schema]),

	CONSTRAINT [un_StockList_ISINCode] UNIQUE ([ISINCode])
)
GO

--進行 MERGE 目的地資料表
CREATE TABLE [dbo].[StockList_Target]
(
	[No]			INT IDENTITY(1,1),
	
	[Schema]		CHAR(4)			NOT NULL,
	[ISINCode]		CHAR(12)		NOT NULL,
	[PublishDate]	DATE			NOT NULL,

	[Name]			NVARCHAR(10)	NOT NULL,
	[Category]		NVARCHAR(10)	NOT NULL,
	[Industry]		NVARCHAR(20)	NULL,
	[CFICode]		VARCHAR(6)		NULL,

	CONSTRAINT [pk_StockList_Target] PRIMARY KEY ([No]),

	CONSTRAINT [un_StockList_Target_Schema] UNIQUE ([Schema]),

	CONSTRAINT [un_StockList_Target_ISINCode] UNIQUE ([ISINCode])
)
GO

--儲存 MERGE 結果資料筆數的資料表
CREATE TABLE [dbo].[MergeRecords]
(
	[No]			INT IDENTITY(1,1),
	[EventDate]		DATETIME DEFAULT (GETDATE()),
	[EventName]		NVARCHAR(50),

	[InsertCount]	SMALLINT,
	[UpdateCount]	SMALLINT,
	[DeleteCount]	SMALLINT,
	
	[Logs]			XML,

	CONSTRAINT [pk_MergeRecords] PRIMARY KEY ([No])
)
GO

